# Editing
def edit(word):
    erab =  ['ٌ','ٍ','،','َ','ُ','ِ','ّ','ۀ','ة','ء','ْ','ْ','ٱ']
    for era in erab:
        if era in word:
            for x in range(word.count(era)):
                k = list(word)
                k.remove(era)
                word = ''.join(k)
    return word
