from sklearn.feature_extraction.text import TfidfVectorizer
from sklearn.feature_extraction.text import CountVectorizer
from sklearn.metrics.pairwise import cosine_similarity
###
tfidf = TfidfVectorizer().fit(AYAT).transform(AYAT).toarray()
count = CountVectorizer().fit(AYAT).transform(AYAT).toarray()
# Formol :
def DisTance(i,ii):
    return round( (5*cosine_similarity([tfidf[i],tfidf[ii]])[0][1]*100 + cosine_similarity([count[i],count[ii]])[0][1]*100)/6 ,1 )

